-- Catalog of authoritative web resources for Malta-focused agent learning.

CREATE TABLE IF NOT EXISTS public.web_knowledge_sources (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  title TEXT NOT NULL,
  url TEXT NOT NULL,
  domain TEXT,
  jurisdiction TEXT[] DEFAULT '{}',
  tags TEXT[] DEFAULT '{}',
  priority INT DEFAULT 100,
  created_at TIMESTAMPTZ DEFAULT now()
);
ALTER TABLE public.web_knowledge_sources ENABLE ROW LEVEL SECURITY;
-- Global read access via RLS helper (users already restricted at application layer).
DO $$
BEGIN
  IF NOT EXISTS (
    SELECT 1 FROM pg_policies WHERE schemaname = 'public' AND tablename = 'web_knowledge_sources' AND policyname = 'web_sources_read') THEN
    EXECUTE 'CREATE POLICY web_sources_read ON public.web_knowledge_sources FOR SELECT USING (true)';
  END IF;
END $$;
